mapper.update:
update wantreview set
review_comment = #{review_comment},
review_date = #{review_date},
review_rate = #{review_rate}
where review_id = #{review_id}

update wantad set
post_review_count = post_review_count + 1 
where post_id=#{post_id}

update wantad set
post_review_count = post_review_count - 1 
where post_id=#{post_id}
